mapper.update:
update b_role
sys_id = #{sysId}, dep_id = #{depId}, role_name = #{roleName},
where flow_id = #{flowId}

update b_system_menu_tree
menu_name = #{menuName}, menu_level = #{menuLevel}, menu_parent_id = #{menuParentId}, path = #{path}, component = #{component}, order_num = #{orderNum}, perms = #{perms}, icon = #{icon}
where flow_id = #{flowId}

update b_permission set query=#{query},edit=#{edit},export=#{export},impower=#{impower}
where menu_id = #{menuParentId}
and role_id = #{roleId}
and user_id = #{userId}
and dep_id = #{depId}

mapper.delete:
delete from b_permission
where sys_id = #{sysId}
and role_id = #{roleId}
and user_id = #{userId}
and dep_id = #{depId}
and menu_id = #{menuId}

mapper.select:
select flow_id,user_id,role_id,sys_id,dep_id,menu_id,menu_parent_id,query,edit,export,impower,create_time
from b_permission
where menu_parent_id = #{menuId}
and role_id = #{roleId}
and user_id = #{userId}
and dep_id = #{depId}

select r.flow_id,r.dep_id,sys_id,role_name,r.create_time,s.sys_name
from b_role r inner join b_user_role ur on r.flow_id=ur.role_id inner join b_system s on r.sys_id=s.flow_id
where ur.user_id=#{userId}

select flow_id,user_id,role_id,sys_id,dep_id,menu_id,menu_parent_id,query,edit,export,impower,create_time
from b_permission p
where user_id=#{userId}

select flow_id,user_id,role_id,sys_id,dep_id,menu_id,menu_parent_id,query,edit,export,impower,create_time
from b_permission
where menu_id = #{menuParentId}
and role_id = #{roleId}
and user_id = #{userId}
and dep_id = #{depId}
